import { useState } from "react";
import emailjs from "@emailjs/browser";
import { jsPDF } from "jspdf";

export default function FaleConosco() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [enviados, setEnviados] = useState([]); // Armazena os envios
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    emailjs
      .send("service_70qhemt", "template_r2zfuxj", form, "L-5ExBufX2oZqn4fA")
      .then(() => {
        alert("✅ Email enviado com sucesso!");
        setEnviados([...enviados, form]); // Salva no histórico
        setForm({ name: "", email: "", message: "" });
      })
      .catch((error) => {
        console.error("❌ Erro ao enviar:", error);
        alert("Erro ao enviar email. Veja o console.");
      })
      .finally(() => {
        setLoading(false);
      });
  };

  // Função para gerar o PDF
  const gerarPDF = () => {
    const doc = new jsPDF();
    doc.setFont("helvetica", "bold");
    doc.text("Relatório de Contatos", 20, 20);

    let y = 40;
    enviados.forEach((item, index) => {
      doc.setFont("helvetica", "normal");
      doc.text(`Contato ${index + 1}`, 20, y);
      doc.text(`Nome: ${item.name}`, 20, y + 10);
      doc.text(`Email: ${item.email}`, 20, y + 20);
      doc.text(`Mensagem: ${item.message}`, 20, y + 30);
      y += 50;
    });

    doc.save("relatorio_contatos.pdf");
  };

  return (
    <div>
      <form
        onSubmit={handleSubmit}
        style={{
          display: "flex",
          flexDirection: "column",
          gap: "10px",
          width: "300px",
        }}
      >
        <input type="text" name="name" placeholder="Seu Nome" value={form.name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="Seu Email" value={form.email} onChange={handleChange} required />
        <textarea name="message" placeholder="Sua Mensagem" value={form.message} onChange={handleChange} required />
        <button type="submit" disabled={loading}>
          {loading ? "Enviando..." : "Enviar"}
        </button>
      </form>

      {enviados.length > 0 && (
        <button onClick={gerarPDF} style={{ marginTop: "20px" }}>
          Gerar PDF
        </button>
      )}
    </div>
  );
}
