import { useState } from "react";
import emailjs from "@emailjs/browser";

export default function FaleConosco() {
  const [form, setForm] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(true);

    emailjs
      .send("service_70qhemt", "template_r2zfuxj", form, "L-5ExBufX2oZqn4fA")
      .then(() => {
        alert("✅ Email enviado com sucesso!");
        setForm({ name: "", email: "", message: "" });
      })
      .catch((error) => {
        console.error("❌ Erro ao enviar:", error);
        alert("Erro ao enviar email. Veja o console.");
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <form
      onSubmit={handleSubmit}
      style={{
        display: "flex",
        flexDirection: "column",
        gap: "10px",
        width: "300px",
      }}
    >
      <input type="text" name="name" placeholder="Seu Nome" value={form.name} onChange={handleChange} required />
      <input type="email" name="email" placeholder="Seu Email" value={form.email} onChange={handleChange} required />
      <textarea name="message" placeholder="Sua Mensagem" value={form.message} onChange={handleChange} required />
      <button type="submit" disabled={loading}>
        {loading ? "Enviando..." : "Enviar"}
      </button>
    </form>
  );
}
