import { useState } from "react";
import emailjs from "@emailjs/browser";

export default function FaleConosco() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [envios, setEnvios] = useState([]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    emailjs
      .send("service_70qhemt", "template_r2zfuxj", form, "SUA_CHAVE_PUBLICA")
      .then(() => {
        alert("Email enviado com sucesso!");
        setEnvios([...envios, form]); // Salva o envio
        setForm({ name: "", email: "", message: "" });
      })
      .catch((error) => console.error("Erro ao enviar:", error));
  };

  const gerarRelatorio = () => {
    const printWindow = window.open("", "_blank");
    printWindow.document.write("<html><head><title>Relatório de Ouvidoria</title></head><body>");
    printWindow.document.write("<h2>Relatório de Ouvidoria</h2>");
    envios.forEach((envio, index) => {
      printWindow.document.write(`<p><strong>${index + 1}. Nome:</strong> ${envio.name}</p>`);
      printWindow.document.write(`<p><strong>Email:</strong> ${envio.email}</p>`);
      printWindow.document.write(`<p><strong>Mensagem:</strong> ${envio.message}</p>`);
      printWindow.document.write("<hr>");
    });
    printWindow.document.write("</body></html>");
    printWindow.document.close();
    printWindow.print();
  };

  return (
    <div>
      <h2>Fale Conosco</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" name="name" placeholder="Seu Nome" value={form.name} onChange={handleChange} required />
        <input type="email" name="email" placeholder="Seu Email" value={form.email} onChange={handleChange} required />
        <textarea name="message" placeholder="Sua Mensagem" value={form.message} onChange={handleChange} required />
        <button type="submit">Enviar</button>
      </form>

      {envios.length > 0 && <button onClick={gerarRelatorio}>Gerar Relatório</button>}
    </div>
  );
}
